(()=>{const e=document.querySelector(".dropdown-button"),t=document.querySelector(".dropdown-menu");new class{constructor(e){this.dropdownElement=e.dropdown,this.openElement=e.open,this.initializeDropdown(),this.toggleDropdown()}checkElements(){return!(!this.dropdownElement||!this.openElement)}setOpenElementStyles(e,t){this.openElement.style.position="absolute",this.openElement.style.top=`${t}px`,this.openElement.style.left=`${e}px`}toggleOpenElement(){this.openElement.classList.contains("visible")?(this.openElement.classList.remove("visible"),this.openElement.style.display="none"):!1===this.openElement.classList.contains("visible")&&(this.openElement.classList.add("visible"),this.openElement.style.display="block")}closeOpenElement(){this.openElement.classList.remove("visible"),this.openElement.style.display="none"}toggleDropdown(){this.dropdownElement.addEventListener("click",(e=>{this.toggleOpenElement(),this.setOpenElementStyles(e.clientX,e.clientY)})),window.addEventListener("click",(e=>{e.target!==this.dropdownElement&&this.openElement.classList.contains("visible")&&this.closeOpenElement()}))}initializeDropdown(){this.openElement.style.display="none"}}({dropdown:e,open:t})})();